function isInSpawnRange (x: number, y: number) {
    if (x < scene.cameraProperty(CameraProperty.Left) - 0) {
        return false
    }
    if (x > scene.cameraProperty(CameraProperty.Right) + 0) {
        return false
    }
    if (y < scene.cameraProperty(CameraProperty.Top) - 0) {
        return false
    }
    if (y > scene.cameraProperty(CameraProperty.Bottom) + 0) {
        return false
    }
    return true
}
